import React,{Component} from 'react'
import './Hero.css'
import 'antd/dist/antd.css';
import { Carousel} from 'antd';
import Icon  from '@ant-design/icons'
import bg1 from '../../assets/images/bg-carosel-1.jpg'
import bg2 from '../../assets/images/bg-carosel-0.jpg'





class HeroCarosel extends Component {
  constructor(props) {
    super(props);
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
    this.carousel = React.createRef();
  }
  next() {
    this.carousel.next();
  }
  previous() {
    this.carousel.prev();
  }

  render() {
    const props = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
    };
    return (
      <div>
        <Icon type="left-circle" onClick={this.previous} className="icons" />
        <Carousel className="container" 
        ref={node => (this.carousel = node)} {...props}>
          <div className="slider-01">
            <h3 >
            <img src={bg1} alt="" className="bgpic"/></h3>
<div classname="container"><div classname="row"><div classname="col-xl-6 col-md-8"><div classname="slide-left"><div classname="slide-sale">20 <span>%<br />off</span></div><div classname="slide-title"><h1>Spring</h1></div><div classname="slide-subtitle">Season festival</div><span classname="slide-since">Catalogue 2018</span></div></div><div classname="col-xl-6 col-md-4 text-right d-none d-md-block"><div classname="slide-offer">Limited time offer</div><a classname="slide-button" href="/sidebar-with-load-more">Get the Offer</a></div></div></div>

            </div>
            
          
          <div>
            <h3 >
              <img src={bg2} alt="" className="bgpic"/>
            </h3>
          </div>
        </Carousel>
        <Icon type="right-circle" onClick={this.next} className="icons" />
      </div>
    );
  }
}
export default HeroCarosel
