    <div>
          <Card
            style={{ width: "90%" }}
             cover={
       <img className="zoom"
        alt={product.name}
        src={product.image}
      />

    }

  > 
    <Meta className="title-top-sell"
      title={product.name}
      description={product.description}

    />
   <p>${product.price}</p>

   <Button onClick={addAlert}


