  <ShoppingCartOutlined /></Button>
   <Button>


   <HeartTwoTone twoToneColor="#eb2f96" />
   </Button>   {
     click ? <Alert message="addet to cart successfuly" type="success" /> : ""
   }
  </Card>
    </div>
  )
}

export default Product
