import React,{Component} from 'react'
import './Hero.css'
import 'antd/dist/antd.css';
import { Carousel} from 'antd';
import Icon  from '@ant-design/icons'
import bg1 from '../../assets/images/bg-carosel-1.jpg'
import bg2 from '../../assets/images/bg-carosel-0.jpg'





class HeroCarosel extends Component {
  constructor(props) {
    super(props);
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
    this.carousel = React.createRef();
  }
  next() {
    this.carousel.next();
  }
  previous() {
    this.carousel.prev();
  }

  render() {
    const props = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
    };
    return (
      <div>
        <Icon type="left-circle" onClick={this.previous} />
        <Carousel className="container"
        ref={node => (this.carousel = node)} {...props}>
          <div>
            <h3 className="contentStyle">1</h3>
            <img src={bg1} alt=""/>
          </div>
          <div>
            <h3 className="contentStyle">2</h3>
          </div>
          <div>
            <h3 className="contentStyle">3</h3>
          </div>
          <div>
            <h3 className="contentStyle">4</h3>
          </div>
        </Carousel>
        <Icon type="right-circle" onClick={this.next} />
      </div>
    );
  }
}
export default HeroCarosel
