import React, { useState } from 'react'
import { Card, Button, Alert,message } from 'antd'
import { ShoppingCartOutlined, HeartOutlined, HeartTwoTone } from '@ant-design/icons';
import 'antd/dist/antd.css';
function Product(props) {
  const { product } = props
  const { Meta } = Card
  const [click, setClick] = useState(false)
  const addAlert = () => {
    setClick(true)
  }
  const success = () => {
    message.success('This is a success message');
    setClick(true)
  };
  console.log(click);
  return (
    <div>
      <Card
        style={{ width: "90%" }}
        cover={
          <img className="zoom"
            alt={product.name}
            src={product.image}
          />

        }

      >
        <Meta className="title-top-sell"
          title={product.name}
          description={product.description}

        />
        <p>${product.price}</p>

        <Button onClick={addAlert}


        >
          <ShoppingCartOutlined /></Button>
        <Button>


          <HeartTwoTone twoToneColor="#eb2f96" />
        </Button>
        {
          click ? <span onClick={success} message="" > </span>: ""
        //<Alert message="addet to cart successfuly" type="success" />
        }
      </Card>
    </div>
  )
}

export default Product
